const initialState = {
  user:{
isLoggedIn:false,
user:null,
signUpData:{},
loginDate:{},
  },
  post:{
    mainPosts:[],
  }
};
export const loginAction = (data)=>{
    return {
        type:'LOG_IN',
        data,

    }
}

// async action creator(비동기 엑션 크리에이터)
// action creator



store.dispatch()
// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action)=>{
    switch (action.type){
        case 'LOG_IN':
            return{
                ...state,
          user:{
            ...state.user,
            isLoggedIn:true,
            user:action.data,
          }
            }
    }
}
